import React, { useState, useCallback } from 'react';
import ReactFlow, {
  Node,
  Edge,
  addEdge,
  Connection,
  useNodesState,
  useEdgesState,
  Controls,
  Background,
  BackgroundVariant,
} from 'reactflow';
import 'reactflow/dist/style.css';

// Component types for the palette
interface ComponentType {
  id: string;
  label: string;
  icon: string;
  color: string;
  description: string;
}

const componentTypes: ComponentType[] = [
  {
    id: 'microservice',
    label: 'Microservice',
    icon: '🔧',
    color: '#3B82F6',
    description: 'Independent service'
  },
  {
    id: 'database',
    label: 'Database',
    icon: '💾',
    color: '#F59E0B',
    description: 'Data storage'
  },
  {
    id: 'cache',
    label: 'Cache',
    icon: '⚡',
    color: '#EF4444',
    description: 'Fast data access'
  },
  {
    id: 'loadbalancer',
    label: 'Load Balancer',
    icon: '⚖️',
    color: '#10B981',
    description: 'Traffic distribution'
  },
  {
    id: 'queue',
    label: 'Message Queue',
    icon: '📬',
    color: '#8B5CF6',
    description: 'Async messaging'
  },
  {
    id: 'apigateway',
    label: 'API Gateway',
    icon: '🚪',
    color: '#06B6D4',
    description: 'Single entry point'
  }
];

// Enhanced initial setup with better styling
const initialNodes: Node[] = [
  {
    id: '1',
    position: { x: 400, y: 100 },
    data: { label: '🚪 API Gateway' },
    style: { 
      background: 'linear-gradient(135deg, #06B6D4, #0891B2)', 
      color: 'white', 
      padding: '16px 20px', 
      borderRadius: '12px', 
      fontWeight: '600',
      fontSize: '14px',
      boxShadow: '0 4px 12px rgba(6, 182, 212, 0.3)',
      border: '2px solid rgba(255, 255, 255, 0.2)',
      minWidth: '140px',
      textAlign: 'center'
    }
  },
  {
    id: '2',
    position: { x: 200, y: 250 },
    data: { label: '🔧 User Service' },
    style: { 
      background: 'linear-gradient(135deg, #3B82F6, #2563EB)', 
      color: 'white', 
      padding: '16px 20px', 
      borderRadius: '12px', 
      fontWeight: '600',
      fontSize: '14px',
      boxShadow: '0 4px 12px rgba(59, 130, 246, 0.3)',
      border: '2px solid rgba(255, 255, 255, 0.2)',
      minWidth: '140px',
      textAlign: 'center'
    }
  },
  {
    id: '3',
    position: { x: 600, y: 250 },
    data: { label: '🔧 Order Service' },
    style: { 
      background: 'linear-gradient(135deg, #3B82F6, #2563EB)', 
      color: 'white', 
      padding: '16px 20px', 
      borderRadius: '12px', 
      fontWeight: '600',
      fontSize: '14px',
      boxShadow: '0 4px 12px rgba(59, 130, 246, 0.3)',
      border: '2px solid rgba(255, 255, 255, 0.2)',
      minWidth: '140px',
      textAlign: 'center'
    }
  },
  {
    id: '4',
    position: { x: 300, y: 400 },
    data: { label: '💾 PostgreSQL' },
    style: { 
      background: 'linear-gradient(135deg, #F59E0B, #D97706)', 
      color: 'white', 
      padding: '16px 20px', 
      borderRadius: '12px', 
      fontWeight: '600',
      fontSize: '14px',
      boxShadow: '0 4px 12px rgba(245, 158, 11, 0.3)',
      border: '2px solid rgba(255, 255, 255, 0.2)',
      minWidth: '140px',
      textAlign: 'center'
    }
  },
  {
    id: '5',
    position: { x: 500, y: 400 },
    data: { label: '⚡ Redis Cache' },
    style: { 
      background: 'linear-gradient(135deg, #EF4444, #DC2626)', 
      color: 'white', 
      padding: '16px 20px', 
      borderRadius: '12px', 
      fontWeight: '600',
      fontSize: '14px',
      boxShadow: '0 4px 12px rgba(239, 68, 68, 0.3)',
      border: '2px solid rgba(255, 255, 255, 0.2)',
      minWidth: '140px',
      textAlign: 'center'
    }
  }
];

const initialEdges: Edge[] = [
  { 
    id: 'e1-2', 
    source: '1', 
    target: '2', 
    animated: true,
    style: { stroke: '#10B981', strokeWidth: 3 }
  },
  { 
    id: 'e1-3', 
    source: '1', 
    target: '3', 
    animated: true,
    style: { stroke: '#10B981', strokeWidth: 3 }
  },
  { 
    id: 'e2-4', 
    source: '2', 
    target: '4',
    style: { stroke: '#6B7280', strokeWidth: 2 }
  },
  { 
    id: 'e3-5', 
    source: '3', 
    target: '5',
    style: { stroke: '#6B7280', strokeWidth: 2 }
  }
];

const SimpleCanvas: React.FC = () => {
  const [nodes, setNodes, onNodesChange] = useNodesState(initialNodes);
  const [edges, setEdges, onEdgesChange] = useEdgesState(initialEdges);
  const [selectedComponent, setSelectedComponent] = useState<ComponentType | null>(null);

  const onConnect = useCallback(
    (params: Connection) => setEdges((eds: Edge[]) => addEdge({
      ...params,
      animated: Math.random() > 0.6,
      style: { 
        stroke: '#10B981', 
        strokeWidth: 2 
      }
    }, eds)),
    [setEdges]
  );

  const onDragOver = useCallback((event: React.DragEvent) => {
    event.preventDefault();
    event.dataTransfer.dropEffect = 'move';
  }, []);

  const onDrop = useCallback((event: React.DragEvent) => {
    event.preventDefault();
    
    if (!selectedComponent) return;

    const reactFlowBounds = (event.target as Element).closest('.react-flow')?.getBoundingClientRect();
    if (!reactFlowBounds) return;

    const position = {
      x: event.clientX - reactFlowBounds.left - 70,
      y: event.clientY - reactFlowBounds.top - 40,
    };

    const newNode: Node = {
      id: `${selectedComponent.id}-${Date.now()}`,
      position,
      data: { label: `${selectedComponent.icon} ${selectedComponent.label}` },
      style: {
        background: `linear-gradient(135deg, ${selectedComponent.color}, ${selectedComponent.color}dd)`,
        color: 'white',
        padding: '16px 20px',
        borderRadius: '12px',
        fontWeight: '600',
        fontSize: '14px',
        boxShadow: `0 4px 12px ${selectedComponent.color}40`,
        border: '2px solid rgba(255, 255, 255, 0.2)',
        minWidth: '140px',
        textAlign: 'center'
      }
    };

    setNodes((nds: Node[]) => nds.concat(newNode));
    setSelectedComponent(null);
  }, [selectedComponent, setNodes]);

  const onDragStart = useCallback((event: React.DragEvent, component: ComponentType) => {
    setSelectedComponent(component);
    event.dataTransfer.effectAllowed = 'move';
  }, []);

  const clearCanvas = useCallback(() => {
    setNodes([]);
    setEdges([]);
  }, [setNodes, setEdges]);

  const autoLayout = useCallback(() => {
    const layoutNodes = nodes.map((node: Node, index: number) => {
      const col = index % 4;
      const row = Math.floor(index / 4);
      return {
        ...node,
        position: {
          x: 150 + col * 200,
          y: 100 + row * 150,
        },
      };
    });
    setNodes(layoutNodes);
  }, [nodes, setNodes]);

  return (
    <div style={{ display: 'flex', height: '100%', background: 'linear-gradient(135deg, #1e293b 0%, #0f172a 100%)' }}>
      {/* Component Palette */}
      <div style={{
        width: '280px',
        background: 'rgba(15, 23, 42, 0.95)',
        backdropFilter: 'blur(10px)',
        borderRight: '1px solid rgba(59, 130, 246, 0.2)',
        padding: '20px',
        overflowY: 'auto'
      }}>
        <div style={{
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          marginBottom: '20px',
          paddingBottom: '15px',
          borderBottom: '1px solid rgba(59, 130, 246, 0.2)'
        }}>
          <h3 style={{
            color: 'white',
            margin: 0,
            fontSize: '1.2rem',
            fontWeight: '600'
          }}>Components</h3>
          <div style={{ display: 'flex', gap: '8px' }}>
            <button 
              onClick={autoLayout}
              style={{
                background: 'rgba(59, 130, 246, 0.2)',
                border: '1px solid rgba(59, 130, 246, 0.3)',
                borderRadius: '6px',
                padding: '6px',
                color: 'white',
                cursor: 'pointer',
                fontSize: '14px'
              }}
              title="Auto Layout"
            >
              📐
            </button>
            <button 
              onClick={clearCanvas}
              style={{
                background: 'rgba(239, 68, 68, 0.2)',
                border: '1px solid rgba(239, 68, 68, 0.3)',
                borderRadius: '6px',
                padding: '6px',
                color: 'white',
                cursor: 'pointer',
                fontSize: '14px'
              }}
              title="Clear Canvas"
            >
              🗑️
            </button>
          </div>
        </div>
        
        {componentTypes.map((component) => (
          <div
            key={component.id}
            draggable
            onDragStart={(e) => onDragStart(e, component)}
            style={{
              display: 'flex',
              alignItems: 'center',
              gap: '12px',
              padding: '12px',
              background: 'rgba(255, 255, 255, 0.05)',
              backdropFilter: 'blur(10px)',
              border: `2px solid ${component.color}40`,
              borderRadius: '10px',
              cursor: 'grab',
              marginBottom: '12px',
              transition: 'all 0.2s ease',
              userSelect: 'none'
            }}
            onMouseEnter={(e) => {
              e.currentTarget.style.background = 'rgba(255, 255, 255, 0.1)';
              e.currentTarget.style.transform = 'translateY(-2px)';
              e.currentTarget.style.boxShadow = `0 4px 12px ${component.color}40`;
            }}
            onMouseLeave={(e) => {
              e.currentTarget.style.background = 'rgba(255, 255, 255, 0.05)';
              e.currentTarget.style.transform = 'translateY(0)';
              e.currentTarget.style.boxShadow = 'none';
            }}
          >
            <div style={{
              width: '40px',
              height: '40px',
              borderRadius: '8px',
              background: component.color,
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              fontSize: '18px'
            }}>
              {component.icon}
            </div>
            <div>
              <div style={{
                color: 'white',
                fontWeight: '600',
                fontSize: '14px',
                marginBottom: '2px'
              }}>
                {component.label}
              </div>
              <div style={{
                color: 'rgba(255, 255, 255, 0.6)',
                fontSize: '12px'
              }}>
                {component.description}
              </div>
            </div>
          </div>
        ))}
        
        <div style={{
          marginTop: '20px',
          paddingTop: '15px',
          borderTop: '1px solid rgba(59, 130, 246, 0.2)',
          color: 'rgba(255, 255, 255, 0.6)',
          fontSize: '12px'
        }}>
          <p style={{ margin: '8px 0', display: 'flex', alignItems: 'center', gap: '6px' }}>
            💡 Drag components to canvas
          </p>
          <p style={{ margin: '8px 0', display: 'flex', alignItems: 'center', gap: '6px' }}>
            🔗 Connect by dragging edges
          </p>
        </div>
      </div>

      {/* Canvas Area */}
      <div style={{ flex: 1, position: 'relative' }}>
        <ReactFlow
          nodes={nodes}
          edges={edges}
          onNodesChange={onNodesChange}
          onEdgesChange={onEdgesChange}
          onConnect={onConnect}
          onDrop={onDrop}
          onDragOver={onDragOver}
          fitView
          style={{ background: 'linear-gradient(135deg, #1e293b 0%, #0f172a 100%)' }}
        >
          <Controls 
            position="bottom-right"
            style={{
              background: 'rgba(15, 23, 42, 0.9)',
              border: '1px solid rgba(59, 130, 246, 0.2)',
              borderRadius: '8px'
            }}
          />
          <Background 
            variant={BackgroundVariant.Dots} 
            gap={20} 
            size={1}
            color="rgba(59, 130, 246, 0.3)"
          />
        </ReactFlow>
      </div>
    </div>
  );
};

export default SimpleCanvas;
