import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { api } from '../api';

interface Problem {
  id: string;
  title: string;
  description: string;
  requirements: string[];
  scale: string;
  difficulty: string;
}

const ProblemSelector: React.FC = () => {
  const [problems, setProblems] = useState<Problem[]>([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchProblems = async () => {
      try {
        const response = await api.getProblems();
        setProblems(response);
      } catch (error) {
        console.error('Failed to fetch problems:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchProblems();
  }, []);

  const handleProblemSelect = (problemId: string) => {
    navigate(`/design/${problemId}`);
  };

  if (loading) {
    return <div className="loading">Loading problems...</div>;
  }

  return (
    <div className="problem-selector">
      <div className="selector-header">
        <h2>Choose a System Design Problem</h2>
        <p>Select a problem to start practicing system design</p>
      </div>

      <div className="problems-grid">
        {problems.map(problem => (
          <div
            key={problem.id}
            className={`problem-card difficulty-${problem.difficulty.toLowerCase()}`}
            onClick={() => handleProblemSelect(problem.id)}
          >
            <div className="problem-header">
              <h3>{problem.title}</h3>
              <span className="difficulty">{problem.difficulty}</span>
            </div>
            
            <p className="problem-description">{problem.description}</p>
            
            <div className="problem-details">
              <div className="scale">
                <strong>Scale:</strong> {problem.scale}
              </div>
              
              <div className="requirements">
                <strong>Key Requirements:</strong>
                <ul>
                  {problem.requirements.slice(0, 3).map((req, idx) => (
                    <li key={idx}>{req}</li>
                  ))}
                  {problem.requirements.length > 3 && (
                    <li>+{problem.requirements.length - 3} more...</li>
                  )}
                </ul>
              </div>
            </div>

            <button className="start-button">
              Start Design
            </button>
          </div>
        ))}
      </div>

      <div className="custom-problem">
        <h3>Or create your own problem</h3>
        <button 
          className="custom-button"
          onClick={() => navigate('/design/custom')}
        >
          Custom Problem
        </button>
      </div>
    </div>
  );
};

export default ProblemSelector;
